--- 练习五

function remove(str,pos,len)
    local len_s = string.len(str)
    if pos + len >= len_s then
        -- pos后数据全部丢掉
        if pos <= 1 then
            return nil
        else
            return string.sub(str,1,pos-1)
        end
    else
        if pos <= 1 then
            return string.sub(str,pos+len+1)
        else
            return string.sub(str,1,pos-1)..string.sub(str,pos+len+1)
        end
    end
end
