print("-------------------exp 7--------------")
function ispali(str)
    local len_s = string.len(str)
    local mid = len_s / 2
    for i = 1,mid,1 do
        j = len_s - i + 1
        if string.sub(str,i,i) ~= string.sub(str,j,j) then
            return false
        end
    end
    return true
end

print(ispali("step on no pets"))
print(ispali("hello world"))
